import React, { useState, useEffect } from 'React';
import axios from 'axios';

function Notice() {

    const [notis, setNotis] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {

        const fetchNotis = async () => {
            try {
                setError(null);
                setNotis(null);

                setLoading(true);
                const response = await axios.get('http://192.168.1.211:8080/api/notice');
                setNotis(response.data.data);
            } catch (e) {
                setError(e);
            }
            setLoading(false);
        };

        fetchNotis();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>An error occurred</div>;
    if (!notis) return null;

    return (
        <div className="card">
            <h2 id={name}><font color="#0066ff">&#9483; </font>{name}</h2>
            <h5>{date}</h5>
            <table>
                {data.map(trs => (
                    <tr>
                    <td class="row-title">
                        <strong>&#x2611; {trs}</strong>
                    </td>
                    { name === "Release" ?
                        <td class="row-click">
                        <a class="n1" href={`/release_note/${trs}_release-note.pdf`} target="_blank">Click</a>
                    </td> : <td></td>
                    }
                    </tr>
                ))}
            </table>
        </div>
    );

}

export default Notice;
