import React, { useState } from 'react';
import './App.css';
import TopNav from './TopNav';
import Notice from './Notice';
import ReleaseNote from './ReleaseNote';
import Utility from './Utility';
import UsefulLinks from './UsefulLinks';
import Download from './Download';
import Manual from './Manual';
import Counter from './Counter';
import logo from './resources/os_logo.png'
import axios from 'axios';

function  Header( {number} ){

  return (
      <div className="header">
          <img src={logo} width="400" height="100"/>
          {/* <h3>TmaxOS Download Page</h3> */}
          <Counter />
      </div>
  );
}

const left_navi = [
  {
    id: 1,
    name: "Notice",
    date: "Aug 1, 2019",
    data: ["TmaxOS 5.0.17 업데이트", "TmaxOS 4.0.1 업데이트"]
  },
  {
    id: 2,
    name: "Release",
    date: "March 5, 2021",
    data: ["TmaxOS_3.5.0", "ToOffice_3.5.0"]
  },
  {
    id: 5,
    name: "Utility",
    data: ["한글2014", "Rufus 2.11"]
  },
  {
    id: 6,
    name: "Links",
    data: ["PDF 변환", "이미지 편집", "압축 풀기"]
  }
];

const right_navi = [
  {
    id: 3,
    name: "Download",
    data: ["TmaxOS 5.0.17", "TmaxOS 4.0.1"]
  },
  {
    id: 4,
    name: "Manual",
    data: ["Installer 생성 가이드", "사용자 안내서 (User Guide)", "프린트 설정 안내서", "Multibooting Guide"]
  },
];

